import React from "react";

function Card(card) {
   function handleCardClick() {
     card.onCardClick(card);
   }
   return (
     <li className="element">
       <img className="element__image" alt={card.name} src={card.link} onClick={handleCardClick} />
       <button type="button" className="element__delete-btn" aria-label="удалить карточку"></button>
       <div className="element__item">
         <h2 className="elements__name">{card.name}</h2>
           <button type="button" className="element__like" aria-label="поставить лайк"></button>
           <span className="element__like-counter">{card.likes.length}</span>
       </div>
     </li>
   )
 }
 export default Card;
