import React from "react";
import PopupWithForm from "./PopupWithForm";

function PopupWithConfirm({ isOpen, onClose, onSubmit, onCardDelete, card }) {
  function handleSubmit() {
    onCardDelete(card);
  }
  return (
    <PopupWithForm
      title="Вы уверены?"
      name="question"
      isOpen={isOpen}
      btnCurrentText="Да"
      onClose={onClose}
      onSubmit={handleSubmit}
      onCardDelete={onCardDelete}
    ></PopupWithForm>
  );
}
export default PopupWithConfirm;
