  return (
    <main className="main">
      <section className="profile">
        <div className="profile__main">
          <div className="profile__overlay">
            <img
              src={userAvatar}
              className="profile__avatar"
              alt="Аватарка"
              style={{ backgroundImage: `url(${userAvatar})` }}
            />
            <div className="profile__avatar-button"></div>
          </div>

          <div className="profile__info">
            <h1 className="profile__info-name">{userName}</h1>
            <button className="profile__info-button" type="button"></button>
            <p className="profile__info-text">{userDescription}</p>
          </div>
        </div>
        <button className="profile__add-button" type="button"></button>
      </section>

      <section className="elements">
        <template id="tmpl-elem">
          <div className="element">
            <img src="#" alt="#" className="element__image" />
            <div className="element__item">
              <h2 className="element__name"></h2>
              <div className="element__like_about">
                <button className="element__like" type="button"></button>
                <button className="element__delete-btn"></button>
                <span className="element__like_counter">0</span>
              </div>
            </div>
          </div>
        </template>
      </section>
    </main>
  );
}

export default Main;
